import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addContact } from './redux/contactsSlice';

const ContactForm = () => {
  const dispatch = useDispatch();
  const contacts = useSelector((state) => state.contacts.items);

  const [name, setName] = useState('');
  const [number, setNumber] = useState('');

  const handleSubmit = () => {
    const normalizedContacts = contacts.map((contact) => contact.name.toLowerCase());

    if (normalizedContacts.includes(name.toLowerCase())) {
      alert(`${name} is already in contacts`);
      return;
    }

    dispatch(addContact({ id: Date.now(), name: name.toLowerCase(), number }));
    setName('');
    setNumber('');
  };

  return (
    <div>
      <input type="text" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} />
      <input type="text" placeholder="Number" value={number} onChange={(e) => setNumber(e.target.value)} />
      <button onClick={handleSubmit}>Add Contact</button>
    </div>
  );
};

export default ContactForm;
